import React from 'react';
import { Button, ToggleButton, Tooltip } from '@mui/material';
import { DarkModeOutlined, LightModeOutlined, GitHub, SearchOutlined } from '@mui/icons-material';


function Navbar() {

  window.onload = function() {

    localStorage.setItem("darkmode", "true"); // get users system settings and appply here

    if (typeof(Storage) !== "undefined") {

      if (localStorage.getItem("darkMode") === "true") {
        document.body.classList.add("dark");
        document.getElementById("")
      } else {
        document.body.classList.remove("dark");
      }
    }

    document.getElementById("darkmode-toggle").addEventListener("click", function() {
        if (localStorage.getItem("darkMode") === "true") {
          localStorage.setItem("darkMode", "false");
          document.body.classList.remove("dark");
        } else {
          localStorage.setItem("darkMode", "true");
          document.body.classList.add("dark");
        }
    }, false);

   document.getElementById("github-button").addEventListener("click", function() {
    window.open(
      "https://www.github.com/popsterrs/weather", "_blank");
   }, false);

   document.getElementById("search-bar").addEventListener("click", function() {
    document.body.classList.toggle("blur");
    document.getElementById("search-container").classList.toggle("show");
   }, false);

   fetch("https://api.openweathermap.org/data/2.5/weather?q=New York,us&appid=1bb73886a8a1fffcd95bb4228ba817ab")
    .then(response => {
      if (!response.ok) {
        throw new Error(response.statusText);
      }
      return response.json();
    })
    .then(data => {
      const temperature = data.main.temp;
      console.log((temperature-273.15) + "°C");
    })
    .catch(error => {
      console.error(error);
  });
  }

  return (
    <nav class="nav-bar">
      <div class="nav-bar-content">
        <div class="nav-bar-left">
          <Button class="nav-bar-button-left">Home</Button>
          <Button class="nav-bar-button-left">Favourites</Button>
          <Button class="nav-bar-button-left">Contact</Button>
        </div>
        <div class="nav-bar-right">
          <Button id="search-bar" class="nav-bar-button-right nav-bar-search-bar" variant="contained-">
          <SearchOutlined class="nav-bar-icon"/>
            <span class="nav-bar-search-bar-text">Search...</span>
          </Button>

          <Tooltip title="GitHub repository">
            <Button id="github-button" class="nav-bar-button-right" variant="outlined">
              <GitHub class="nav-bar-icon"/>
            </Button>
          </Tooltip>

          <Tooltip title="Turn on light mode">
            <Button id="darkmode-toggle" class="nav-bar-button-right" variant="outlined">
              <LightModeOutlined id="sun" class="nav-bar-icon"/>
              <DarkModeOutlined id="moon" class="nav-bar-icon"/>
            </Button>
          </Tooltip>
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
