import React from 'react';
import { Button } from '@mui/material';
import { SearchOutlined } from '@mui/icons-material';

function Search() {

  window.onload = function () {
    document.getElementById("escape").addEventListener("click", function() {
      document.getElementById("blur-container").classList.toggle("blur");
      document.getElementById("search-container").classList.toggle("show");
    }, false);


    window.addEventListener('keydown', function (e) {
      if (e.key === 'Escape') {
        document.getElementById("blur-container").classList.toggle("blur");
        document.getElementById("search-container").classList.toggle("show");
      }
   }, false);  
  };

  return (
    <div id="search-container" class="search-container">
        <div class="search-container-top">
          <SearchOutlined class="nav-bar-icon nav-bar-search-icon search-container-top-icon"/>
          <input type="text" placeholder="Search..."></input>
          <Button id="escape" class="nav-bar-button-right" variant="outlined">
            esc
          </Button>
        </div>
        <div class="search-container-bottom">

        </div>
    </div>
  );
}

export default Search;
