import React from 'react';
import { Button } from '@mui/material';

function WeatherDisplay() {

    window.onload = function() {
        var location = "New York";
        var temperature = 2;


        fetch("https://api.openweathermap.org/data/2.5/weather?q=New York,us&appid=1bb73886a8a1fffcd95bb4228ba817ab")
        .then(response => {
            if (!response.ok) {
            throw new Error(response.statusText);
            }
            return response.json();
        })
        .then(data => {
            temperature = data.main.temp;
            console.log(temperature);
            document.getElementById("temperature").innerHTML = (Math.round(temperature * 10) / 10) +"c";

        })
        .catch(error => {
            console.error(error);
        });
    }



  return (
    <div class="weather-display-container">
        <span id="location" class="weather-display-location-title">New York</span>
        <span id="temperature" class="weather-display-temperture">31.2Â°C</span>
    </div>
  );
}

export default WeatherDisplay;
